package com.example.habitapp.ui

import android.os.Bundle
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.ViewCompat
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.fragment.app.Fragment
import com.example.habitapp.R
import com.google.android.material.bottomnavigation.BottomNavigationView

class MainActivity : AppCompatActivity() {

    private val fragmentHabitos: Fragment = HabitsFragment()
    private val fragmentTareas: Fragment = TasksFragment()
    private val fragmentEstadisticas: Fragment = StatsFragment()
    private val fragmentAjustes: Fragment = SettingsFragment()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        WindowCompat.setDecorFitsSystemWindows(window, false)

        setContentView(R.layout.activity_main)

        WindowCompat.getInsetsController(window, window.decorView).apply {
            isAppearanceLightStatusBars = false
        }

        val container = findViewById<View>(R.id.fragment_contenedor)
        val barraNavegacion = findViewById<BottomNavigationView>(R.id.barra_navegacion)

        ViewCompat.setOnApplyWindowInsetsListener(container) { v, insets ->
            val navBars = insets.getInsets(WindowInsetsCompat.Type.navigationBars())
            v.setPadding(0, 0, 0, navBars.bottom)
            insets
        }

        ViewCompat.setOnApplyWindowInsetsListener(barraNavegacion) { v, insets ->
            val navBars = insets.getInsets(WindowInsetsCompat.Type.navigationBars())
            v.setPadding(v.paddingLeft, v.paddingTop, v.paddingRight, navBars.bottom)
            insets
        }

        mostrarFragmento(fragmentEstadisticas)

        barraNavegacion.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_habitos -> mostrarFragmento(fragmentHabitos)
                R.id.nav_tareas -> mostrarFragmento(fragmentTareas)
                R.id.nav_estadisticas -> mostrarFragmento(fragmentEstadisticas)
                R.id.nav_ajustes -> mostrarFragmento(fragmentAjustes)
            }
            true
        }
    }

    private fun mostrarFragmento(fragmento: Fragment) {
        supportFragmentManager.beginTransaction()
            .replace(R.id.fragment_contenedor, fragmento)
            .commit()
    }
}

package com.example.habitapp.ui

import android.os.Bundle
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.ViewCompat
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.fragment.app.Fragment
import com.example.habitapp.R
import com.example.habitapp.data.db.AppDatabase
import com.example.habitapp.viewmodel.DaoProvider
import com.google.android.material.bottomnavigation.BottomNavigationView

class MainActivity : AppCompatActivity() {

    private val fragmentHabitos: Fragment = HabitsFragment()
    private val fragmentTareas: Fragment = TasksFragment()
    private val fragmentEstadisticas: Fragment = StatsFragment()
    private val fragmentAjustes: Fragment = SettingsFragment()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // Inicializar el UsuarioDao para el ViewModel
        val db = AppDatabase.getInstance(applicationContext)
        DaoProvider.usuarioDao = db.usuarioDao()

        WindowCompat.setDecorFitsSystemWindows(window, false)

        setContentView(R.layout.activity_main)

        WindowCompat.getInsetsController(window, window.decorView).apply {
            isAppearanceLightStatusBars = false
        }

        val container = findViewById<View>(R.id.fragment_contenedor)
        val barraNavegacion = findViewById<BottomNavigationView>(R.id.barra_navegacion)

        ViewCompat.setOnApplyWindowInsetsListener(container) { v, insets ->
            val navBars = insets.getInsets(WindowInsetsCompat.Type.navigationBars())
            v.setPadding(0, 0, 0, navBars.bottom)
            insets
        }

        ViewCompat.setOnApplyWindowInsetsListener(barraNavegacion) { v, insets ->
            val navBars = insets.getInsets(WindowInsetsCompat.Type.navigationBars())
            v.setPadding(v.paddingLeft, v.paddingTop, v.paddingRight, navBars.bottom)
            insets
        }

        mostrarFragmento(fragmentEstadisticas)

        barraNavegacion.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_habitos -> mostrarFragmento(fragmentHabitos)
                R.id.nav_tareas -> mostrarFragmento(fragmentTareas)
                R.id.nav_estadisticas -> mostrarFragmento(fragmentEstadisticas)
                R.id.nav_ajustes -> mostrarFragmento(fragmentAjustes)
            }
            true
        }
    }

    private fun mostrarFragmento(fragmento: Fragment) {
        supportFragmentManager.beginTransaction()
            .replace(R.id.fragment_contenedor, fragmento)
            .commit()
    }
}

package com.example.habitapp.ui
import android.os.Bundle import android.view.LayoutInflater import android.view.View import android.view.ViewGroup import android.widget.TextView import androidx.core.view.ViewCompat import androidx.core.view.WindowInsetsCompat import androidx.fragment.app.Fragment import androidx.lifecycle.ViewModelProvider import com.example.habitapp.R import com.example.habitapp.viewmodel.SettingsViewModel
class SettingsFragment : Fragment() { private lateinit var viewModel: SettingsViewModel
override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? { val view = inflater.inflate(R.layout.fragment_settings, container, false)
val header = view.findViewById<View>(R.id.header_settings) val headerTitle = header.findViewById<TextView>(R.id.tv_header_title) val headerSubtitle = header.findViewById<TextView>(R.id.tv_header_subtitle)
ViewCompat.setOnApplyWindowInsetsListener(header) { v, insets -> val statusBarHeight = insets.getInsets(WindowInsetsCompat.Type.systemBars()).top v.setPadding( v.paddingLeft, statusBarHeight + 24, v.paddingRight, v.paddingBottom ) insets }
headerTitle.text = "Configuración" headerSubtitle.text = "Ajustes de la aplicación"
viewModel = ViewModelProvider(requireActivity())[SettingsViewModel::class.java]
val swNot = view.findViewById<androidx.appcompat.widget.SwitchCompat>(R.id.switch_notifications) val swDark = view.findViewById<androidx.appcompat.widget.SwitchCompat>(R.id.switch_darkmode) val swRem = view.findViewById<androidx.appcompat.widget.SwitchCompat>(R.id.switch_reminders)
viewModel.state.observe(viewLifecycleOwner) { state -> swNot.isChecked = state.notifications swDark.isChecked = state.darkMode swRem.isChecked = state.reminders }
swNot.setOnCheckedChangeListener { _, isChecked -> viewModel.setNotifications(isChecked) } swDark.setOnCheckedChangeListener { _, isChecked -> viewModel.setDarkMode(isChecked) } swRem.setOnCheckedChangeListener { _, isChecked -> viewModel.setReminders(isChecked) }
return view } }

package com.example.habitapp.ui
import android.app.AlertDialog import android.os.Bundle import android.view.LayoutInflater import android.view.View import android.view.ViewGroup import android.widget.EditText import android.widget.TextView import androidx.core.view.ViewCompat import androidx.core.view.WindowInsetsCompat import androidx.fragment.app.Fragment import androidx.lifecycle.ViewModelProvider import com.example.habitapp.R import com.example.habitapp.viewmodel.SettingsViewModel import kotlinx.coroutines.launch
class SettingsFragment : Fragment() { private lateinit var viewModel: SettingsViewModel
override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? { val view = inflater.inflate(R.layout.fragment_settings, container, false)
val header = view.findViewById<View>(R.id.header_settings) val headerTitle = header.findViewById<TextView>(R.id.tv_header_title) val headerSubtitle = header.findViewById<TextView>(R.id.tv_header_subtitle)
ViewCompat.setOnApplyWindowInsetsListener(header) { v, insets -> val statusBarHeight = insets.getInsets(WindowInsetsCompat.Type.systemBars()).top v.setPadding( v.paddingLeft, statusBarHeight + 24, v.paddingRight, v.paddingBottom ) insets }
headerTitle.text = "Configuración" headerSubtitle.text = "Ajustes de la aplicación"
viewModel = ViewModelProvider(requireActivity())[SettingsViewModel::class.java]
val swNot = view.findViewById<androidx.appcompat.widget.SwitchCompat>(R.id.switch_notifications) val swDark = view.findViewById<androidx.appcompat.widget.SwitchCompat>(R.id.switch_darkmode) val swRem = view.findViewById<androidx.appcompat.widget.SwitchCompat>(R.id.switch_reminders)
viewModel.state.observe(viewLifecycleOwner) { state -> swNot.isChecked = state.notifications swDark.isChecked = state.darkMode swRem.isChecked = state.reminders }
swNot.setOnCheckedChangeListener { _, isChecked -> viewModel.setNotifications(isChecked) } swDark.setOnCheckedChangeListener { _, isChecked -> viewModel.setDarkMode(isChecked) } swRem.setOnCheckedChangeListener { _, isChecked -> viewModel.setReminders(isChecked) }
val tvNombreUsuario = view.findViewById<TextView>(R.id.tv_nombre_usuario) viewModel.nombreUsuario.observe(viewLifecycleOwner) { nombre -> tvNombreUsuario.text = nombre } viewModel.cargarNombreUsuario(1L)
val btnEditarPerfil = view.findViewById<View>(R.id.btn_editar_perfil) btnEditarPerfil.setOnClickListener { val editText = EditText(requireContext()) editText.hint = "Nuevo nombre" editText.setText(tvNombreUsuario.text) AlertDialog.Builder(requireContext()) .setTitle("Editar nombre de perfil") .setView(editText) .setPositiveButton("Guardar") { _, _ -> val nuevoNombre = editText.text.toString().trim() viewModel.actualizarNombreUsuario(1L, nuevoNombre) } .setNegativeButton("Cancelar", null) .show() }
return view } }

package com.example.habitapp.viewmodel

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel

data class SettingsState(val notifications: Boolean = true, val darkMode: Boolean = false, val reminders: Boolean = true)

class SettingsViewModel : ViewModel() {
    private val _state = MutableLiveData(SettingsState())
    val state: LiveData<SettingsState> = _state

    fun setNotifications(enabled: Boolean) { _state.value = _state.value?.copy(notifications = enabled) }
    fun setDarkMode(enabled: Boolean) { _state.value = _state.value?.copy(darkMode = enabled) }
    fun setReminders(enabled: Boolean) { _state.value = _state.value?.copy(reminders = enabled) }
}

package com.example.habitapp.viewmodel

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import kotlinx.coroutines.launch

data class SettingsState(val notifications: Boolean = true, val darkMode: Boolean = false, val reminders: Boolean = true)

object DaoProvider {
    lateinit var usuarioDao: com.example.habitapp.data.dao.UsuarioDao
}

class SettingsViewModel : ViewModel() {
    private val _state = MutableLiveData(SettingsState())
    val state: LiveData<SettingsState> = _state

    private val _nombreUsuario = MutableLiveData<String>()
    val nombreUsuario: LiveData<String> = _nombreUsuario

    fun cargarNombreUsuario(id: Long) {
        viewModelScope.launch {
            val usuario = DaoProvider.usuarioDao.getById(id)
            _nombreUsuario.postValue(usuario?.nombre ?: "")
        }
    }

    fun actualizarNombreUsuario(id: Long, nuevoNombre: String) {
        viewModelScope.launch {
            val usuario = DaoProvider.usuarioDao.getById(id)
            if (usuario != null) {
                DaoProvider.usuarioDao.update(usuario.copy(nombre = nuevoNombre))
                _nombreUsuario.postValue(nuevoNombre)
            }
        }
    }

    fun setNotifications(enabled: Boolean) { _state.value = _state.value?.copy(notifications = enabled) }
    fun setDarkMode(enabled: Boolean) { _state.value = _state.value?.copy(darkMode = enabled) }
    fun setReminders(enabled: Boolean) { _state.value = _state.value?.copy(reminders = enabled) }
}
